// src/HomePage.tsx

import React, { useContext } from 'react';
import './HomePage.css';
import { Link, Element } from 'react-scroll';
import { ThemeContext } from './themeContext';

const HomePage: React.FC = () => {
    const { theme, toggleTheme } = useContext(ThemeContext);

    return (
        <div className="home-page">
            <div className="navbar">
                <Link activeClass="active" to="section1" spy={true} smooth={true} offset={-70} duration={500}>
                    Section 1
                </Link>
                <Link activeClass="active" to="section2" spy={true} smooth={true} offset={-70} duration={500}>
                    Section 2
                </Link>
                <button onClick={toggleTheme} className="theme-toggle" style={{ backgroundColor: theme.toggle }}>Toggle Theme</button>
            </div>

            <Element name="section1" className="element">
                <h1>Welcome to Section 1</h1>
                <button className="interactive-button" style={{ backgroundColor: theme.button }}>Click Me</button>
            </Element>

            <Element name="section2" className="element">
                <h1>Welcome to Section 2</h1>
                <button className="interactive-button" style={{ backgroundColor: theme.button }}>Click Me</button>
            </Element>
        </div>
    );
}

export default HomePage;
