// src/themeContext.tsx

import React from 'react';

export const themes = {
  light: {
    foreground: '#000000',
    background: '#eeeeee',
    button: '#87CEFA', // darker shade of light blue
    toggle: '#0000FF', // blue
  },
  dark: {
    foreground: '#ffffff',
    background: '#222222',
    button: '#87CEFA', // darker shade of light blue
    toggle: '#0000FF', // blue
  },
};

export const ThemeContext = React.createContext({
  theme: themes.light,
  toggleTheme: () => {},
});
