// src/themeContext.tsx

import React from 'react';

export const themes = {
  light: {
    foreground: '#000000',
    background: '#eeeeee',
    button: '#87CEFA', // darker shade of light blue
    toggle: '#0000FF', // blue
    p: '#000000', // black
    section: '#d3d3d3', // light gray
    sectionHome: '#d3d3d3', // light gray
    sectionResume: '#d3d3d3', // light gray
    sectionProjects: '#d3d3d3', // light gray
    sectionPhotography: '#d3d3d3', // light gray
  },
  dark: {
    foreground: '#ffffff',
    background: '#222222',
    button: '#87CEFA', // darker shade of light blue
    toggle: '#0000FF', // blue
    p: '#000000', // black
    section: 'black', // dark gray
    sectionHome: '#d3d3d3', // light gray
    sectionResume: '#d3d3d3', // light gray
    sectionProjects: '#d3d3d3', // light gray
    sectionPhotography: '#d3d3d3', // light gray
  },
};

export const ThemeContext = React.createContext({
  theme: themes.light,
  toggleTheme: () => {},
});



