// src/themeContext.tsx

import React from 'react';

export const themes = {
  light: {
    foreground: '#000000',
    background: '#eeeeee',
    button: '#add8e6', // light blue
  },
  dark: {
    foreground: '#ffffff',
    background: '#222222',
    button: '#add8e6', // light blue
  },
};

export const ThemeContext = React.createContext({
  theme: themes.light,
  toggleTheme: () => {},
});
