// src/themeContext.tsx

import React from 'react';

export const themes = {
  light: {
    foreground: '#000000',
    background: '#eeeeee',
    button: '#87CEFA', // darker shade of light blue
    toggle: '#0000FF', // blue
    sectionHome: '#d3d3d3', // light gray
  },
  dark: {
    foreground: '#ffffff',
    background: '#222222',
    button: '#87CEFA', // darker shade of light blue
    toggle: '#0000FF', // blue
    section: '#4b4b4b', // dark gray
  },
};

export const ThemeContext = React.createContext({
  theme: themes.light,
  toggleTheme: () => {},
});
